mapper.select:
select * from demand t1 , demandType t2 where t1.demandTypeid = t2.demandTypeid

      AND t2.demandTypeName = #{type}


      AND t1.predictPrice  >=  #{leftPrice}


      AND t1.predictPrice  <=  #{rightPrice}


      AND t1.predictTime  >=  #{leftTime}


      AND t1.predictTime  <=  #{rightTime}

limit #{count}, 10;
